import requestsimport urllib3import randomfrom bs4 import BeautifulSoupdef download_image():    url = 'https://dvmn.org/media/Requests_Python_Logo.png'    filename = 'img.png'    response = requests.get(url)    response.raise_for_status()    with open(filename, 'wb') as file:        file.write(response.content)  def downloading_book():    id_book = 1    while id_book != 10:        url = 'https://tululu.org/txt.php?id={}'.format(id_book)        filename = 'book/book{}.txt'.format(id_book)        id_book +=1        response = requests.get(url, verify=False)        response.raise_for_status()        with open(filename, 'w') as file:            file.write(response.text)if __name__ == '__main__':    download_image()    downloading_book()